type Color = {
  name: string;
  value: string;
  description?: string;
};

// ğŸ¨ shadcn í† í° ìƒ‰ìƒ íƒ€ì¼ ì»´í¬ë„ŒíŠ¸ (ë‹¤í¬ëª¨ë“œ ê°ì§€ í¬í•¨)
const ColorTile = ({ value }: Pick<Color, "value">) => {
  const [colorValue, setColorValue] = useState("");

  useEffect(() => {
    // ğŸŒ“ CSS ë³€ìˆ˜ ê°’ì„ ì½ì–´ì„œ í‘œì‹œìš©ìœ¼ë¡œë§Œ ì‚¬ìš©
    const updateColor = () => {
      const styles = getComputedStyle(document.documentElement);
      const cssValue = styles.getPropertyValue(value);
      setColorValue(cssValue.trim());
    };

    // ì´ˆê¸° ë¡œë“œ ì‹œ ìƒ‰ìƒ ì½ê¸°
    updateColor();

    // ğŸ”„ MutationObserverë¡œ ë‹¤í¬ëª¨ë“œ ì‹¤ì‹œê°„ ê°ì§€
    const observer = new MutationObserver((mutations) => {
      mutations.forEach((mutation) => {
        if (
          mutation.type === "attributes" &&
          mutation.attributeName === "class"
        ) {
          updateColor();
        }
      });
    });

    observer.observe(document.documentElement, {
      attributes: true,
      attributeFilter: ["class"],
    });

    return () => {
      observer.disconnect();
    };
  }, [value]);

  // ğŸ¯ ëª©ì : CSS ë³€ìˆ˜ëª…ì„ Tailwind í´ë˜ìŠ¤ëª…ìœ¼ë¡œ ë³€í™˜
  // ğŸ“ ë¡œì§: Tailwind v4ëŠ” bg-{name} í˜•ì‹ì„ ìë™ìœ¼ë¡œ --color-{name}ìœ¼ë¡œ ë§¤í•‘
  //   - --primary â†’ bg-primary â†’ --color-primary
  //   - --chart-1 â†’ bg-chart-1 â†’ --color-chart-1
  //   - --primary-foreground â†’ bg-primary-foreground â†’ --color-primary-foreground
  const getBackgroundClass = (cssVariable: string): string => {
    const varName = cssVariable.replace(/^--/, "");
    return `bg-${varName}`;
  };

  const bgClass = getBackgroundClass(value);

  return (
    <div className="flex flex-col items-center gap-2">
      <div className={`size-20 rounded-md border ${bgClass}`} />
      <p className="text-center text-xs opacity-70">{value}</p>
      <p className="text-center font-mono text-xs">{colorValue}</p>
    </div>
  );
};

