/**
 * ğŸ¨ Tailwind Palette: Tailwind CSS ì „ì²´ ìƒ‰ìƒ íŒ”ë ˆíŠ¸
 *
 * Tailwind CSSì˜ 22ê°œ ìƒ‰ìƒ ì‹œìŠ¤í…œ (ê° 11ê°œ shade: 50-950)ê³¼ shadcn í† í°ì„ í•¨ê»˜ ë³´ì—¬ì¤ë‹ˆë‹¤.
 * ë‹¤í¬ëª¨ë“œ ì „í™˜ ì‹œ shadcn í† í° ê°’ì´ ì‹¤ì‹œê°„ìœ¼ë¡œ ì—…ë°ì´íŠ¸ë©ë‹ˆë‹¤.
 */
export const TailwindPalette: Story = {
  args: {
    colors: [],
  },
  render: () => {
    const [tokens, setTokens] = useState<Record<string, string>>({});
    const [isDarkMode, setIsDarkMode] = useState(false);

    // ğŸŒ“ CSS ë³€ìˆ˜ ê°’ì„ ê°€ì ¸ì˜¤ëŠ” í•¨ìˆ˜ (ë‹¤í¬ëª¨ë“œ ì‹¤ì‹œê°„ ê°ì§€)
    const updateTokens = () => {
      const styles = getComputedStyle(document.documentElement);
      const tokenList = [
        "--background",
        "--foreground",
        "--primary",
        "--primary-foreground",
        "--secondary",
        "--secondary-foreground",
        "--muted",
        "--muted-foreground",
        "--accent",
        "--accent-foreground",
        "--destructive",
        "--destructive-foreground",
        "--border",
        "--input",
        "--ring",
        "--card",
        "--card-foreground",
        "--popover",
        "--popover-foreground",
        "--chart-1",
        "--chart-2",
        "--chart-3",
        "--chart-4",
        "--chart-5",
        "--sidebar",
        "--sidebar-foreground",
        "--sidebar-primary",
        "--sidebar-primary-foreground",
        "--sidebar-accent",
        "--sidebar-accent-foreground",
        "--sidebar-border",
        "--sidebar-ring",
        "--surface",
        "--surface-foreground",
        "--code",
        "--code-foreground",
        "--code-highlight",
        "--code-number",
        "--selection",
        "--selection-foreground",
      ];
      const newValues: Record<string, string> = {};
      tokenList.forEach((token) => {
        newValues[token] = styles.getPropertyValue(token).trim();
      });
      setTokens(newValues);
      setIsDarkMode(document.documentElement.classList.contains("dark"));
    };

    // ğŸ”„ ì´ˆê¸° ë¡œë“œ ë° ë‹¤í¬ëª¨ë“œ ë³€ê²½ ê°ì§€
    useEffect(() => {
      updateTokens();

      // MutationObserverë¡œ í´ë˜ìŠ¤ ë³€ê²½ ê°ì§€
      const observer = new MutationObserver((mutations) => {
        mutations.forEach((mutation) => {
          if (
            mutation.type === "attributes" &&
            mutation.attributeName === "class"
          ) {
            updateTokens();
          }
        });
      });

      observer.observe(document.documentElement, {
        attributes: true,
        attributeFilter: ["class"],
      });

      return () => {
        observer.disconnect();
      };
    }, []);

    // ë‹¨ì¼ ì»¬ëŸ¬ ë°•ìŠ¤ ì»´í¬ë„ŒíŠ¸
    const ColorBox = ({ name, value }: { name: string; value: string }) => (
      <div key={name} style={{ textAlign: "center" }}>
        <div
          style={{
            background: value || "transparent",
            width: "100%",
            height: "60px",
            borderRadius: "8px",
            border: isDarkMode ? "1px solid #333" : "1px solid #e5e5e5",
          }}
        />
        <p style={{ fontSize: "12px", marginTop: "4px" }}>{name}</p>
        <code style={{ fontSize: "10px" }}>{value || "-"}</code>
      </div>
    );

    // Tailwind íŒ”ë ˆíŠ¸ ë Œë”ë§ í•¨ìˆ˜
    const renderTailwindColors = (
      colors: Record<string, string | Record<string, string>>,
      parent = "",
    ) => {
      return Object.entries(colors).map(([name, value]) => {
        const key = parent ? `${parent}-${name}` : name;
        if (typeof value === "string") {
          return <ColorBox key={key} name={key} value={value} />;
        }
        if (typeof value === "object") {
          return (
            <div key={key} style={{ marginBottom: "2rem", width: "100%" }}>
              <h3
                style={{ marginBottom: "0.5rem", textTransform: "capitalize" }}
              >
                {name}
              </h3>
              <div
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(11, 1fr)",
                  gap: "0.5rem",
                }}
              >
                {renderTailwindColors(value, name)}
              </div>
            </div>
          );
        }
        return null;
      });
    };

    // ğŸ¯ ëª©ì : Tailwind í´ë˜ìŠ¤ ë§¤í•‘ìœ¼ë¡œ shadcn í† í° í‘œì‹œ
    // ğŸ“ ë¡œì§: Tailwind v4ëŠ” bg-{name} í˜•ì‹ì„ ìë™ìœ¼ë¡œ --color-{name}ìœ¼ë¡œ ë§¤í•‘
    //   - --primary â†’ bg-primary â†’ --color-primary
    //   - --chart-1 â†’ bg-chart-1 â†’ --color-chart-1
    //   - --sidebar â†’ bg-sidebar â†’ --color-sidebar
    //   - --surface â†’ bg-surface â†’ --color-surface
    const tokenClassMap: Record<string, string> = {
      "--background": "bg-background",
      "--foreground": "bg-foreground",
      "--primary": "bg-primary",
      "--primary-foreground": "bg-primary-foreground",
      "--secondary": "bg-secondary",
      "--secondary-foreground": "bg-secondary-foreground",
      "--muted": "bg-muted",
      "--muted-foreground": "bg-muted-foreground",
      "--accent": "bg-accent",
      "--accent-foreground": "bg-accent-foreground",
      "--destructive": "bg-destructive",
      "--destructive-foreground": "bg-destructive-foreground",
      "--border": "bg-border",
      "--input": "bg-input",
      "--ring": "bg-ring",
      "--card": "bg-card",
      "--card-foreground": "bg-card-foreground",
      "--popover": "bg-popover",
      "--popover-foreground": "bg-popover-foreground",
      "--chart-1": "bg-chart-1",
      "--chart-2": "bg-chart-2",
      "--chart-3": "bg-chart-3",
      "--chart-4": "bg-chart-4",
      "--chart-5": "bg-chart-5",
      "--sidebar": "bg-sidebar",
      "--sidebar-foreground": "bg-sidebar-foreground",
      "--sidebar-primary": "bg-sidebar-primary",
      "--sidebar-primary-foreground": "bg-sidebar-primary-foreground",
      "--sidebar-accent": "bg-sidebar-accent",
      "--sidebar-accent-foreground": "bg-sidebar-accent-foreground",
      "--sidebar-border": "bg-sidebar-border",
      "--sidebar-ring": "bg-sidebar-ring",
      "--surface": "bg-surface",
      "--surface-foreground": "bg-surface-foreground",
      "--code": "bg-code",
      "--code-foreground": "bg-code-foreground",
      "--code-highlight": "bg-code-highlight",
      "--code-number": "bg-code-number",
      "--selection": "bg-selection",
      "--selection-foreground": "bg-selection-foreground",
    };

    return (
      <div>
        {/* í˜„ì¬ í…Œë§ˆ í‘œì‹œ */}
        <div
          style={{
            marginBottom: "1rem",
            padding: "0.5rem",
            background: isDarkMode ? "#333" : "#f5f5f5",
            borderRadius: "4px",
          }}
        >
          <strong>Current Theme:</strong>{" "}
          {isDarkMode ? "Dark Mode" : "Light Mode"}
        </div>

        {/* shadcn í† í° */}
        <h2 style={{ marginBottom: "1rem" }}>Shadcn Design Tokens</h2>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(6, 1fr)",
            gap: "1rem",
            marginBottom: "2rem",
          }}
        >
          {Object.entries(tokenClassMap).map(([token, className]) => {
            const value = tokens[token];
            return (
              <div key={token} style={{ textAlign: "center" }}>
                <div
                  className={className}
                  style={{
                    width: "100%",
                    height: "60px",
                    borderRadius: "8px",
                    border:
                      token === "--card" || token === "--background"
                        ? isDarkMode
                          ? "2px solid #666"
                          : "2px solid #ccc"
                        : isDarkMode
                          ? "1px solid #333"
                          : "1px solid #e5e5e5",
                  }}
                />
                <p style={{ fontSize: "12px", marginTop: "4px" }}>{token}</p>
                <code style={{ fontSize: "10px" }}>
                  {value || "loading..."}
                </code>
              </div>
            );
          })}
        </div>

        {/* Tailwind íŒ”ë ˆíŠ¸ */}
        <h2 style={{ marginBottom: "1rem" }}>Tailwind Color Palette</h2>
        {renderTailwindColors(tailwindColors)}
      </div>
    );
  },
};

