/**
 * ğŸ¯ ëª©ì : CPU ì‚¬ìš©ëŸ‰ ì‹œê³„ì—´ ë°ì´í„° ìƒì„±
 * CAST AI ìŠ¤íƒ€ì¼ì˜ í´ëŸ¬ìŠ¤í„° ëª¨ë‹ˆí„°ë§ ë°ì´í„°
 */
const generateCpuTimeSeriesData = () => {
  const data = [];
  const startDate = new Date("2024-11-01");

  for (let i = 0; i < 60; i++) {
    const date = new Date(startDate);
    date.setDate(startDate.getDate() + i);

    // CPU ë°ì´í„° - ë³€ë™ì„± ìˆëŠ” íŒ¨í„´
    const baseProvisioned = 2500 + Math.random() * 500;
    const baseRequested = 1800 + Math.random() * 400;
    const baseUsed = 800 + Math.random() * 600 + (i > 30 ? 200 : 0);

    data.push({
      date: date.toISOString().split("T")[0],
      provisioned: Math.round(baseProvisioned),
      requested: Math.round(baseRequested),
      used: Math.round(baseUsed),
    });
  }
  return data;
};

/**
 * ğŸ¯ ëª©ì : ë¹„ìš© ì‹œê³„ì—´ ë°ì´í„° ìƒì„±
 */
const generateCostTimeSeriesData = () => {
  const data = [];
  const startDate = new Date("2024-11-01");

  for (let i = 0; i < 60; i++) {
    const date = new Date(startDate);
    date.setDate(startDate.getDate() + i);

    // ë¹„ìš© ë°ì´í„° - ì ì§„ì  ì¦ê°€ íŒ¨í„´
    const baseProvCost = 0.02 + Math.random() * 0.015;
    const baseReqCost = 0.03 + Math.random() * 0.02;
    const baseUsedCost = 0.04 + Math.random() * 0.025;

    data.push({
      date: date.toISOString().split("T")[0],
      provCost: Number(baseProvCost.toFixed(5)),
      reqCost: Number(baseReqCost.toFixed(5)),
      usedCost: Number(baseUsedCost.toFixed(5)),
    });
  }
  return data;
};

const cpuChartData = generateCpuTimeSeriesData();
